dunes-script-module.script:
var networks = new Array;

switch(type) {
    case "P":
        networks.push("vra1");
        networks.push("vra2");
        break;
    case "T":
        networks.push("vra3");
        break;
    case "V":
        networks.push("vra4");
        break;
    case "D":
        networks.push("vra5");
        networks.push("vra6");
        break;
}

return networks;
