dunes-script-module.script:
var locations = new Array;

switch(country) {
    case "DK":
        locations.push("CPH");
        locations.push("AAR");
        locations.push("ODE");
        break;
    case "NO":
        locations.push("OSL");
        locations.push("BER");
        break;
    case "SE":
        locations.push("STO");
        locations.push("GOT");
        break;
    case "UK":
        locations.push("LON");
        locations.push("MAN");
        break;
}

return locations;
