workflow-item.script:
var namePart = vm.name.substring(0,vm.name.length - 2);
//System.log("t1 : " + namePart)
var goodHost = true;
for each (host in cluster.host) {
    goodHost = true;
    if (oldVmHost == host.name) {
        goodHost = false;
        //System.log(host.name +" No good");
    } else {
        for each (searchvm in host.vm) {
            //System.log(host.name +" checked");
            if (searchvm.name.search(namePart) == 0 && searchvm.name != vm.name) {
                goodHost = false;
                //System.log(host.name +" No good");
                break;
            }
        }
    }
    
    if (goodHost) {
        esxiHost = host;
        break
    }
}
