workflow-item.script:
var cpu = vm.cpu.split(" ",1);
var memory = vm.memory.split(" ",1);
rightSize = false;

if (catAndTags.get("Atea Right Size vCPU") != null) {
    if (catAndTags.get("Atea Right Size vCPU") != cpu) {
        if (cpu == catAndTags.get("Atea Right Size vCPU")) {
            System("no change to CPU");
        } else {
            if (catAndTags.get("Atea Right Size Max. vCPU") != null) {
                if (catAndTags.get("Atea Right Size vCPU") < catAndTags.get("Atea Right Size Max. vCPU")) {
                    if (catAndTags.get("Atea Right Size Min. vCPU") != null) {
                        if (catAndTags.get("Atea Right Size vCPU") > catAndTags.get("Atea Right Size Min. vCPU")) {
                            configCPU = catAndTags.get("Atea Right Size vCPU");
                            rightSize = true;
                        }
                    }
                    else {
                        configCPU = catAndTags.get("Atea Right Size vCPU");
                        rightSize = true;
                    }
                    
                }
            
            }
            else {
                if (catAndTags.get("Atea Right Size Min. vCPU") != null) {
                    if (catAndTags.get("Atea Right Size vCPU") > catAndTags.get("Atea Right Size Min. vCPU")) {
                        configCPU = catAndTags.get("Atea Right Size vCPU");
                        rightSize = true;
                    }
                }
                else {
                    configCPU = catAndTags.get("Atea Right Size vCPU");
                    rightSize = true;
                }
            }
        }
    }
}

if (catAndTags.get("Atea Right Size Memory") != null) {
    if (catAndTags.get("Atea Right Size Memory") != memory) {
        if (cpu == catAndTags.get("Atea Right Size Memory")) {
            System("no change to Mmemory")
        } else {
            if (catAndTags.get("Atea Right Size Max. Memory") != null) {
                if (catAndTags.get("Atea Right Size Memory") < catAndTags.get("Atea Right Size Max. Memory")) {
                    if (catAndTags.get("Atea Right Size Min. Memory") != null) {
                        if (catAndTags.get("Atea Right Size Memory") > catAndTags.get("Atea Right Size Min. Memory")) {
                            configMem = catAndTags.get("Atea Right Size Memory");
                            rightSize = true;
                        }
                    }
                    else {
                        configMem = catAndTags.get("Atea Right Size Memory");
                        rightSize = true;
                    }
                    
                }
            
            }
            else {
                if (catAndTags.get("Atea Right Size Min. Memory") != null) {
                    if (catAndTags.get("Atea Right Size Memory") > catAndTags.get("Atea Right Size Min. Memory")) {
                        configMem = catAndTags.get("Atea Right Size Memory")
                        rightSize = true;
                    }
                }
                else {
                    configMem = catAndTags.get("Atea Right Size Memory");
                    rightSize = true;
                }
            }
        }
    }
}

configMem   = configMem * 1024;

if (catAndTags.get("Atea Right Size") == "true") {
    rightSize = true;
} else {
    rightSize = false
}
